    }

    public static int yahtzee(int... dice) {
        int[] counts = countDiceOccurrences(dice);
        for (int count : counts) {
            if (count == 5) {
                return 50;
            }
        }
        return 0;
    }

    private static int sumOfSpecificValue(int value, int... dice) {
        int sum = 0;
        for (int die : dice) {
            if (die == value) {
                sum += value;
            }
        }
        return sum;
    }

    private static int[] countDiceOccurrences(int... dice) {
        int[] counts = new int[6];
        for (int die : dice) {
            counts[die - 1]++;
        }
        return counts;
    }

    public static int ones(int... dice) {
        return sumOfSpecificValue(1, dice);
    }

    public static int twos(int... dice) {
        return sumOfSpecificValue(2, dice);
    }

    public static int threes(int... dice) {
        return sumOfSpecificValue(3, dice);
    }

    protected int[] dice;

    public Yahtzee(int d1, int d2, int d3, int d4, int d5) {
        dice = new int[]{d1, d2, d3, d4, d5};
    }

    public int fours() {
        return sumOfSpecificValue(4, dice);
    }

    public int fives() {
        return sumOfSpecificValue(5, dice);
    }

    public int sixes() {
        return sumOfSpecificValue(6, dice);
    }

    public static int score_pair(int... dice) {
        int[] counts = countDiceOccurrences(dice);
        for (int i = counts.length - 1; i >= 0; i--) {
            if (counts[i] >= 2) {
                return (i + 1) * 2;
            }
        }
        return 0;
    }

    public static int two_pair(int... dice) {
        int[] counts = countDiceOccurrences(dice);
        int pairs = 0;
        int score = 0;
        for (int i = counts.length - 1; i >= 0; i--) {
            if (counts[i] >= 2) {
                pairs++;
                score += (i + 1) * 2;
                if (pairs == 2) {
                    return score;
                }
            }
        }
        return 0;
    }

    public static int four_of_a_kind(int... dice) {
        int[] counts = countDiceOccurrences(dice);
        for (int i = 0; i < counts.length; i++) {
            if (counts[i] >= 4) {
                return (i + 1) * 4;
            }
        }
        return 0;
    }

    public static int three_of_a_kind(int... dice) {
        int[] counts = countDiceOccurrences(dice);
        for (int i = 0; i < counts.length; i++) {
            if (counts[i] >= 3) {
                return (i + 1) * 3;
            }
        }
        return 0;
    }

    public static int smallStraight(int... dice) {
        int[] counts = countDiceOccurrences(dice);
        for (int i = 0; i < 5; i++) {
            if (counts[i] != 1) {
                return 0;
            }
        }
        return 15;
    }

    public static int largeStraight(int... dice) {
        int[] counts = countDiceOccurrences(dice);
        for (int i = 1; i < 6; i++) {
            if (counts[i] != 1) {
                return 0;
            }
        }
        return 20;
    }

    public static int fullHouse(int... dice) {
        int[] counts = countDiceOccurrences(dice);
        boolean hasThree = false;
        boolean hasTwo = false;
        int threeValue = 0;
        int twoValue = 0;
        for (int i = 0; i < counts.length; i++) {
            if (counts[i] == 3) {
                hasThree = true;
                threeValue = i + 1;
            } else if (counts[i] == 2) {
                hasTwo = true;
                twoValue = i + 1;
            }
        }
        if (hasThree && hasTwo) {
            return threeValue * 3 + twoValue * 2;
        }
        return 0;
    }
}

